import React, { useEffect, useState } from "react";
import { socketService } from "../services/socketService";
import "../styles/PlayerCounter.css";

const PlayerCounter: React.FC = () => {
  const [count, setCount] = useState(0);

  useEffect(() => {
    // ✅ Match backend event name exactly
    socketService.on("playerCount", (newCount: number) => {
      setCount(newCount);
    });

    // ask server for initial count (optional)
    socketService.emit("getPlayerCount");

    return () => {
      socketService.off("playerCount");
    };
  }, []);

  return (
    <div className="player-counter">
      <div className="player-indicator">
        <span className="online-dot"></span>
        <span className="player-text">
          {count} {count === 1 ? "Player" : "Players"} Online
        </span>
      </div>
    </div>
  );
};

export default PlayerCounter;
