import React, { useEffect, useState } from "react";
import { socketService } from "./services/socketService";
import PlayerCounter from "./components/PlayerCounter";
import Grid from "./components/Grid";
import CooldownTimer from "./components/CooldownTimer";
import CharacterModal from "./components/CharacterModal";
import HistorySlider from "./components/HistorySlider";

interface Cell {
  value: string;
  playerId: string | null;
}

interface HistoryEntry {
  grid: Cell[][];
  timestamp: number;
}

const App: React.FC = () => {
  const [grid, setGrid] = useState<Cell[][]>(
    Array(10)
      .fill(null)
      .map(() => Array(10).fill({ value: "", playerId: null }))
  );
  const [selectedCell, setSelectedCell] = useState<{
    row: number;
    col: number;
  } | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [history, setHistory] = useState<HistoryEntry[]>([]);

  useEffect(() => {
    console.log("🧠 Connecting to socket...");
    socketService.connect();

    socketService.on("initialGrid", (serverGrid: Cell[][]) => {
      console.log("📦 Got initial grid from server");
      setGrid(serverGrid);
    });

    socketService.on("gridUpdated", (newGrid: Cell[][]) => {
      console.log("♻️ Grid updated from server");
      setGrid(newGrid);
      setHistory((prev) => [...prev, { grid: newGrid, timestamp: Date.now() }]);
    });

    return () => {
      socketService.off("initialGrid");
      socketService.off("gridUpdated");
      socketService.disconnect();
    };
  }, []);

  const handleCellClick = (row: number, col: number) => {
    setSelectedCell({ row, col });
    setIsModalOpen(true);
  };

  const handleSubmitCharacter = (character: string) => {
    if (!selectedCell) return;
    const { row, col } = selectedCell;

    const updatedGrid = grid.map((r, rowIndex) =>
      r.map((cell, colIndex) =>
        rowIndex === row && colIndex === col
          ? {
              ...cell,
              value: character,
              playerId: socketService.getSocketId(), // ✅ FIXED: use a getter
            }
          : cell
      )
    );

    setGrid(updatedGrid);
    setIsModalOpen(false);
    setSelectedCell(null);

    // 🔹 Emit update to backend
    socketService.emit("updateCell", { row, col, value: character });
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedCell(null);
  };

  return (
    <div className="App text-center">
      <h1 className="text-2xl font-bold mb-2">Multiplayer Grid App</h1>
      <PlayerCounter />
      <Grid grid={grid} onCellClick={handleCellClick} />
      <CooldownTimer endTime={Date.now() + 5000} />

      {isModalOpen && (
        <CharacterModal
          onSubmit={handleSubmitCharacter}
          onClose={handleCloseModal}
          selectedCell={selectedCell}
        />
      )}

      <HistorySlider
        history={history}
        onTimeChange={(index) => setGrid(history[index].grid)}
      />
    </div>
  );
};

export default App;
