import React, { useEffect, useState } from "react";
import { socketService } from "../services/socketService";

const PlayerCounter: React.FC = () => {
  const [playersOnline, setPlayersOnline] = useState(0);

  useEffect(() => {
    const handlePlayerCount = (count: number) => {
      setPlayersOnline(count);
    };

    // ✅ ensure socket is connected before listening
    if (!socketService.isConnected()) {
      socketService.connect();
    }

    socketService.on("playerCount", handlePlayerCount);

    return () => {
      socketService.off("playerCount", handlePlayerCount);
    };
  }, []);

  return (
    <div className="player-counter">
      <h2>{playersOnline} Players Online</h2>
    </div>
  );
};

export default PlayerCounter;
