// client/src/services/socketService.ts
import { io, Socket } from "socket.io-client";

class SocketService {
  private socket: Socket | null = null;

  connect(): void {
    if (this.socket && this.socket.connected) {
      // already connected
      console.log("âš™ï¸ socket already connected:", this.socket.id);
      return;
    }

    this.socket = io("http://localhost:3001", {
      transports: ["websocket"],
      reconnection: true,
    });

    this.socket.on("connect", () => {
      console.log("âœ… socket connected (client):", this.socket?.id);
    });

    this.socket.on("disconnect", (reason) => {
      console.log("â›” socket disconnected (client):", reason);
    });

    this.socket.on("connect_error", (err) => {
      console.error("âš ï¸ socket connect_error:", err);
    });
  }

  disconnect(): void {
    if (this.socket) {
      console.log("ðŸ”Œ disconnecting socket (client):", this.socket.id);
      this.socket.disconnect();
      this.socket = null;
    }
  }

  on(event: string, callback: (...args: any[]) => void): void {
    this.socket?.on(event, callback);
  }

  off(event: string, callback?: (...args: any[]) => void): void {
    if (!this.socket) return;
    if (callback) this.socket.off(event, callback);
    else this.socket.off(event);
  }

  emit(event: string, data?: any): void {
    this.socket?.emit(event, data);
  }

  isConnected(): boolean {
    return !!(this.socket && this.socket.connected);
  }
}

export const socketService = new SocketService();
