import { io, Socket } from "socket.io-client";

class SocketService {
  private socket: Socket | null = null;

  connect(): void {
    // ✅ Prevent duplicate connections (React StrictMode double-mount fix)
    if (this.socket && this.socket.connected) {
      console.log("⚠️ Socket already connected");
      return;
    }

    this.socket = io("http://localhost:3001", {
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
    });

    this.socket.on("connect", () => {
      console.log("✅ Connected to server:", this.socket?.id);
    });

    this.socket.on("disconnect", () => {
      console.log("❌ Disconnected from server");
    });

    this.socket.on("connect_error", (error) => {
      console.error("Connection error:", error);
    });
  }

  disconnect(): void {
    if (this.socket) {
      console.log("👋 Disconnecting socket:", this.socket.id);
      this.socket.disconnect();
      this.socket = null;
    }
  }

  on(event: string, callback: (...args: any[]) => void): void {
    if (this.socket) {
      this.socket.on(event, callback);
    }
  }

  emit(event: string, data: any): void {
    if (this.socket) {
      this.socket.emit(event, data);
    }
  }

  off(event: string): void {
    if (this.socket) {
      this.socket.off(event);
    }
  }

  updateCell(row: number, col: number, value: string): void {
    this.emit("update-cell", { row, col, value });
  }

  getHistory(): void {
    this.emit("get-history", {});
  }

  timeTravel(timestamp: number): void {
    this.emit("time-travel", { timestamp });
  }

  isConnected(): boolean {
    return this.socket !== null && this.socket.connected;
  }
}

export const socketService = new SocketService();
