import React, { useEffect, useState } from "react";
import { socketService } from "../services/socketService";

const PlayerCounter: React.FC = () => {
  const [count, setCount] = useState(0);

  useEffect(() => {
    const handlePlayerCount = (num: number) => {
      console.log("ðŸ“Š Player count updated:", num);
      setCount(num);
    };

    socketService.on("playerCount", handlePlayerCount);

    return () => {
      socketService.off("playerCount");
    };
  }, []);

  return <h2>Players online: {count}</h2>;
};

export default PlayerCounter;
